import React, { useRef, useEffect } from "react";
import "./App.css";
import * as faceapi from "face-api.js";
import { BrowserRouter, Route, useHistory } from "react-router-dom"; // Import useHistory hook
import Homepage from "./Homepage";

// Define the App component
export function App() {
  const videoRef = useRef();
  const canvasRef = useRef();
  const history = useHistory(); // Use useHistory hook

  // LOAD FROM USEEFFECT
  useEffect(() => {
    startVideo();
    videoRef && loadModels();
  }, []);

  // OPEN YOU FACE WEBCAM
  const startVideo = () => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((currentStream) => {
        videoRef.current.srcObject = currentStream;
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // LOAD MODELS FROM FACE API
  const loadModels = () => {
    Promise.all([
      // THIS FOR FACE DETECT AND LOAD FROM YOU PUBLIC/MODELS DIRECTORY
      faceapi.nets.tinyFaceDetector.loadFromUri("/models"),
      faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
      faceapi.nets.faceRecognitionNet.loadFromUri("/models"),
      faceapi.nets.faceExpressionNet.loadFromUri("/models"),
    ]).then(() => {
      faceMyDetect();
    });
  };

  // Face detection logic
  const faceMyDetect = () => {
    let straightCounter = 0; // Counter for tracking consecutive frames with a straight face
    let faceVisible = false; // Flag to track if the face is visible

    setInterval(async () => {
      const detections = await faceapi
        .detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceExpressions();

      // Check if at least one face is detected
      if (detections.length > 0) {
        // Increment straightCounter if face is straight
        if (isFaceStraight(detections[0])) {
          straightCounter++;
        } else {
          straightCounter = 0; // Reset counter if face is not straight
        }

        // Check if face is fully visible (you can customize this logic)
        if (isFaceFullyVisible(detections[0])) {
          faceVisible = true;
        }

        // Check if conditions are met for redirecting to another page
        if (straightCounter >= 3 && faceVisible) {
          // Redirect to another page
          history.push("/Homepage"); // Use history.push instead of window.location.href
        }
      } else {
        // Reset counters if no face is detected
        straightCounter = 0;
        faceVisible = false;
      }

      // Draw detections on canvas
      canvasRef.current.innerHtml = faceapi.createCanvasFromMedia(
        videoRef.current
      );
      faceapi.matchDimensions(canvasRef.current, {
        width: 940,
        height: 650,
      });

      const resized = faceapi.resizeResults(detections, {
        width: 940,
        height: 650,
      });

      faceapi.draw.drawDetections(canvasRef.current, resized);
      faceapi.draw.drawFaceLandmarks(canvasRef.current, resized);
      faceapi.draw.drawFaceExpressions(canvasRef.current, resized);
    }, 1000);
  };

  // Function to check if the face is straight
  const isFaceStraight = (detection) => {
    // Check if detection object and annotations are available
    if (detection && detection.annotations && detection.annotations.face && detection.annotations.face[0]) {
      const faceAnnotation = detection.annotations.face[0];
      // Check if 'roll' property is available
      if (faceAnnotation && faceAnnotation.roll !== undefined) {
        const { roll } = faceAnnotation;
        // Assuming a range of -10 to 10 degrees for a straight face
        return roll >= -10 && roll <= 10;
      }
    }
    return false; // Return false if face detection data is not available or incomplete
  };

  // Function to check if the face is fully visible
  const isFaceFullyVisible = (detection) => {
    // Assuming that a fully visible face requires both eyes, nose, and mouth to be detected
    return (
      detection.landmarks.getLeftEye() &&
      detection.landmarks.getRightEye() &&
      detection.landmarks.getNose() &&
      detection.landmarks.getMouth()
    );
  };

  // Render the App component
  return (
    <div className="myapp">
      <h1>Face Detection</h1>
      <div className="appvide">
        <video crossOrigin="anonymous" ref={videoRef} autoPlay></video>
      </div>
      <canvas
        ref={canvasRef}
        width="940"
        height="650"
        className="appcanvas"
      />
    </div>
  );
}

// Define the Router component
export function Router() {
  return (
    <BrowserRouter>
      <Route exact path="/" component={App} />
      <Route exact path="/Homepage" component={Homepage} />
    </BrowserRouter>
  );
}
